from flask import Flask, render_template, request
from playhouse.flask_utils import object_list

from app.consts.const import RAN, TECH_POINT_URL, QUEUED, QUILL_URL
from app.models.base import Job
from app.threads.mediator import Mediator
from app.websites.techpoint.techpointparser import TechPointParser

app = Flask(__name__, template_folder='template')



@app.route('/mediator')
def mediator():
    print('Mediator Initiating')
    mediate = Mediator()
    print('Mediator Initiated')
    mediate.run()
    return 'dONE'


@app.route('/seed')
def seed():
    data = []
    for datum in data:
        try:
            job = Job()
            job.url = datum
            job.status = RAN
            job.save()
            print(f'Saving {job.url}')
        except Exception as e:
            print(e)
    return 'dONE'

